import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:flutter/services.dart';
import 'package:shared_preferences/shared_preferences.dart';
import 'package:webview_flutter/webview_flutter.dart';


class HelpScreen extends StatefulWidget {
  final data;
  HelpScreen({String this.data});

  @override
  HelpScreenState createState() {
    return HelpScreenState();
  }
}

class HelpScreenState extends State<HelpScreen> {
  WebViewController _controller;

  @override
  Widget build(BuildContext context) {
//    _loadHtmlFromAssets();
//    print(widget.data);
    return Scaffold(
      appBar: AppBar(
          title: Text('Resenhando'),
        backgroundColor: Colors.transparent,
      ),
      body: WebView(
        initialUrl: '',
        onWebViewCreated: (WebViewController webViewController) {
          _controller = webViewController;
          _loadHtmlFromAssets(widget.data);
        },
      ),
    );
  }

  _loadHtmlFromAssets(String data) async {
//    String fileText = await rootBundle.loadString('assets/help.html');
//    String fileText = "<div> "
//        "<p>Taken from wikpedia</p>"
//        "<img src='data:image/png;base64,'/>"
//        "</div>";

    final prefs = await SharedPreferences.getInstance ();

    String noInternet = '<!DOCTYPE html> '
        '<html lang="pt-br"> '
        '<head> '
        '<title>Sem Internet</title> '
        '<meta charset="utf-8">'
        '</head> '
        '<body> '
        '<div align="center"> '
        '<img src=\'data:image/png;base64,\'/> '
        '<h3>Não foi possível conectar a Internet.</h4>'
        '<h4>Por favor reinicie o aplicativo conectado a Internet para que seja exibido o conteúdo.</h4>'
        '</div>'
        '</body>'
        '</html>';


    String myString = prefs.getString (data) ?? noInternet;
//    String fileText = '<html><body>hello world</body></html>';
  //  print(data);
  //  print(myString);

    _controller.loadUrl( Uri.dataFromString(
        myString,
        mimeType: 'text/html',
        encoding: Encoding.getByName('utf-8')
    ).toString());
//  _controller.loadUrl(fileText);

  }
}